import { Component, AfterViewInit, Component, ElementRef, Renderer2, ViewChild } from '@angular/core';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.scss']
})
export class NavbarComponent {

  document.addEventListener('DOMContentLoaded', () => {
    const burgerMenu = document.querySelector('.burger-menu') as HTMLElement;
    const nav = document.querySelector('.nav') as HTMLElement;

    burgerMenu.addEventListener('click', () => {
      nav.classList.toggle('active');
    });
  });

}

<header>
  <div class="container">
      <div class="logo">
          <img src="tu_logo.png" alt="Logo" width="30" height="30">
          <span>Mi Sitio</span>
      </div>
      <nav>
          <ul class="nav">
              <li><a href="#">Inicio</a></li>
              <li><a href="#">Acerca de nosotros</a></li>
              <li><a href="#">Servicios</a></li>
              <li><a href="#">Contacto</a></li>
          </ul>
          <div class="burger-menu">&#9776;</div>
      </nav>
  </div>
</header>
